/**
 * Contains queries used in media commands.
 */
const fetch = require('node-fetch')

module.exports = class Query {
    constructor(type, variables) {
        this.type = type
        this.variables = variables
    }

    /**
     * Performs a query.
     * @returns {Promise<Response>} The response generated by performing the query.
     */
    performQuery() {
        return fetch(this.type.endpoint, this.type.options(this.variables))
    }
}
